       function closeNav() {
            var element = document.getElementsByTagName("body");
      console.log(element)
              if (element[0].classList.contains("sidebar-collapse")) {
                      element[0].classList.remove("sidebar-collapse");
                  } else {
                      element[0].classList.add("sidebar-collapse");
                      element[0].classList.add("sidebar-closed");
                  }
                  if (element[0].classList.contains("sidebar-open")) {
                      element[0].classList.remove("sidebar-open");
                      element[0].classList.add("sidebar-closed");
                  } else {
                      element[0].classList.add("sidebar-open");
                      element[0].classList.remove("sidebar-closed");
                      element[0].classList.remove("sidebar-collapse");
                  }
      }
      $(document).ready(function() {
      $('.select2-multi').select2({
      tags: true,
      theme: "classic",
      dropdownParent: $('#select-multi_container'),
      maximumSelectionLength: 5,
      });
      $('.select2-multi').on('select2:opening select2:closing', function( event ) {
          var $searchfield = $(this).parent().find('.select2-search__field');
          $searchfield.prop('disabled', true);
      });

      });
